import React from 'react';

const months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
class Calender extends React.Component {
    constructor(props) {
        super(props);

        const date = new Date();
        this.state = {
             month : months[date.getMonth()],
             year : date.getFullYear(),
             day : date.getDay()
        };
     }

    renderYear = () => {
       var year = 1990;
        return (
            Array.from( new Array(41), (v,i) =>
            <option key={i} value={year+i}>{year+i}</option>)
        );
    }

    renderMonth = () => {
        return (
            months.map((m,i) => (
                <option key={i} value={m}>{m}</option>)
            )
        );
    }

    renderDays = () => {
        return (
            <tr>
                <th>{'Sun'}</th>
                <th>{'Mon'}</th>
                <th>{'Tue'}</th>
                <th>{'Wed'}</th>
                <th>{'Thu'}</th>
                <th>{'Fri'}</th>
                <th>{'Sat'}</th>
            </tr>
        );
    }

    renderDates = () => {
        console.log(this.state.year,this.state.month,this.state.day);
        return (
            <tr>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
            </tr>
        );
    }

    setYear = (e) => {
        this.setState({
            year: e.target.value
        })
    }

    setMonth = (e) => {
        this.setState({
            month: e.target.value
        })
    }

    render() {
        return (
            <div className='heading'>
                <br />
                &nbsp;<b>Calender</b>
                &nbsp; 
                <select defaultValue={this.state.year} onChange={this.setYear}>
                    {this.renderYear()}
                </select>
                &nbsp;&nbsp; 
                <select defaultValue={this.state.month} onChange={this.setMonth}>
                     {this.renderMonth()}
                </select>
                <div className='table'>
                    <table className='students'>
                        <tbody>
                            {this.renderDays()}
                            {this.renderDates()}
                        </tbody>
                    </table>
              </div>
            </div>
        );
    }
}

export default Calender;
