import React from 'react';

class Calender extends React.Component {
    constructor(props) {
        super(props);    
        this.state = {
             months : '',
             years : ''

        };
     }

    renderYear = () => {
       var year = 1990;
        return (
            Array.from( new Array(41), (v,i) =>
            <option key={i} value={year+i}>{year+i}</option>)
        );
    }

    renderYear = () => {
        return (
            this.state.years.map((m,i) => (
                <option key={i} value={m}>{m}</option>)
            )
        )
    }

    renderMonth = () => {
        return (
            this.state.months.map((m,i) => (
                <option key={i} value={m}>{m}</option>)
            )
        );
    }

    renderDays = () => {
        return (
            <tr>
                <th>{'Sun'}</th>
                <th>{'Mon'}</th>
                <th>{'Tue'}</th>
                <th>{'Wed'}</th>
                <th>{'Thu'}</th>
                <th>{'Fri'}</th>
                <th>{'Sat'}</th>
            </tr>
        );
    }

    renderDates = () => {
        return (
            <tr>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
            </tr>
        );
    }
    render() {
        return (
            <div className='heading'>
                <br />
                &nbsp;<b>Calender</b>
                &nbsp; 
                <select>
                    {this.renderYear()}
                </select>
                &nbsp;&nbsp; 
                <select>
                     {this.renderMonth()}
                </select>
                <div className='table'>
                    <table className='students'>
                        <tbody>
                            {this.renderDays()}
                            {this.renderDates()}
                        </tbody>
                    </table>
              </div>
            </div>
        );
    }
}

export default Calender;
